mapper.select:
SELECT * 
FROM (
    SELECT A.*, ROWNUM AS rnum
    FROM (
        SELECT * 
        FROM posts
        WHERE post_type = #{post_type}
        ORDER BY post_idx DESC
    ) A
    WHERE ROWNUM <= #{startRow} + #{pageSize}
)
WHERE rnum > #{startRow}

SELECT COUNT(*)
FROM posts
WHERE post_type = #{post_type}
